//write function compress(char *)
#include <stdio.h>
#include <stdlib.h>
#include <math.h>


char* compress (char strSource[]){

	char* strPtr = strSource;
	char* countPtr = strSource;

	int strIndex = 0;
	int cntIndex = 0;
	int count = 0;
	char lastChar = strPtr[0];
	int tenPowJ;
	int j;
	int firstDigitFound = 0;

	while( strPtr[strIndex] != '\0' ){
		if(strPtr[strIndex]==lastChar){
			count++;
		}
		else{

			countPtr[cntIndex]=lastChar;
			cntIndex++;
			firstDigitFound = 0;
			if(count>1){
				for(j=10; j>=0; j--){
					tenPowJ = pow(10,j);

					if(count/tenPowJ != 0){
						firstDigitFound = 1;
					}

					if(firstDigitFound == 1){
						 countPtr[cntIndex++] = 48+count/tenPowJ;
						 count = count%tenPowJ;
					}

				}
				firstDigitFound = 0;
						
			}
			count = 1;
		}

		lastChar = strPtr[strIndex];
		strIndex++;

	}
	strSource[cntIndex+1] = '\0';

	return strSource;
}

int main(int argc, char* argv[]){
	char strSource[] = "";

	printf("%s\n", compress(strSource));
	return 0;
}